import React, { Component } from "react";
import axios from "axios";

class Home extends Component {
  constructor() {
    super();
    this.state = {
      projects: [],
      selectedProject: { id: "0", name: "All tasks" },
      projectInput: false,
      projectInputValue: "All tasks",
    };
    this.listiliseProjects = this.listiliseProjects.bind(this);
    this.selectProject = this.selectProject.bind(this);
    this.nameOrInput = this.nameOrInput.bind(this);
    this.submitProjectName = this.submitProjectName.bind(this);
  }

  componentDidMount() {
    this.getProjects();
  }

  getProjects() {
    let config = {
      headers: {
        Authorization: "Bearer " + this.props.token,
      },
    };
    axios
      .get("/api/projects", config)
      .then((response) => {
        this.setState({ projects: response.data.projects });
      })
      .catch((error) => {
        console.error(error);
      });
  }

  listiliseProjects(projects) {
    return projects.map((project) => (
      <li
        className={
          this.state.selectedProject.id === project._id
            ? "project selected"
            : "project"
        }
        key={project._id}
        onClick={() => {
          this.selectProject(project._id, project.name);
        }}
      >
        {project.name ? project.name : "<untitled>"}
      </li>
    ));
  }

  selectProject(id, name) {
    this.setState({ selectedProject: { id, name }, projectInputValue: name });
  }

  nameOrInput(condition) {
    if (condition && !(this.state.selectedProject.id === "0")) {
      return (
        <form class="project-name-form" onSubmit={this.submitProjectName}>
          <input
            name="projectname"
            id="projectname"
            autoFocus
            type="text"
            class="project-name-input"
            value={this.state.projectInputValue}
            onChange={(e) => {
              this.setState({ projectInputValue: e.target.value });
            }}
          ></input>
          <button class="login" type="submit">
            <span className="material-symbols-outlined edit-icon">check</span>
          </button>
        </form>
      );
    } else {
      return (
        <h2
          className="project-name"
          onClick={() => {
            this.setState({ projectInput: true });
          }}
        >
          {this.state.selectedProject.name}
          {this.state.selectedProject.id !== "0" ? (
            <span className="material-symbols-outlined edit-icon">edit</span>
          ) : (
            ""
          )}
        </h2>
      );
    }
  }

  submitProjectName(e) {
    e.preventDefault();
    let body = {
      projectname: this.state.projectInputValue,
      id: this.state.selectedProject.id,
    };
    let config = {
      headers: {
        Authorization: "Bearer " + this.props.token,
      },
    };

    axios
      .post("/api/project/edit", body, config)
      .then((response) => {
        this.getProjects();
        this.setState({
          selectedProject: {
            id: response.data.project.id,
            name: response.data.project.name,
          },
          projectInput: false,
        });
      })
      .catch((error) => {
        console.error(error);
      });
  }

  render() {
    return (
      <div className="home">
        <aside className="sidebar">
          <div className="default-projects-container">
            <ul className="default-projects">
              <li
                className={
                  this.state.selectedProject.id === "0"
                    ? "project selected"
                    : "project"
                }
                onClick={() => {
                  this.selectProject("0", "All tasks");
                }}
              >
                <span className="material-symbols-outlined">list</span>All tasks
              </li>
            </ul>
          </div>
          <div className="projects-container">
            <h2>
              Projects{" "}
              <a
                href="/project/add"
                className="material-symbols-outlined edit-icon"
              >
                add
              </a>
            </h2>
            <ul className="projects">
              {this.listiliseProjects(this.state.projects)}
            </ul>
          </div>
        </aside>
        <div className="tasks-container">
          {this.nameOrInput(this.state.projectInput)}
          <div className="tasks">
            <ul className="task-list">
              <li className="task">
                <div className="task-left">
                  <span className="task-name">Example task</span>
                </div>
                <div className="task-right">
                  <span className="material-symbols-outlined repeat-icon">
                    cycle
                  </span>
                  <span className="material-symbols-outlined edit-icon">
                    edit
                  </span>

                  <span className="due-date">10/12/22</span>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
